const methods = {
    getAll: require('./funcs/getAll.js'),
    getCountry: require('./funcs/getCountry.js'),
    getState: require('./funcs/getState.js')
}

module.exports = {
    version: "1.0.6",
    getAll: function() {
        return execute('getAll', {});
    },
    getCountry: function(country, sort) {
        return execute('getCountry', { country, sort });
    },
    getState: function(state, sort) {
        return execute('getState', { state, sort });
    }
}

function execute(method, params) {
 
    if (!params.sort) params.sort = null;
    if (!params.country) params.country = null;
    if (!params.state) params.state = null;

    return methods[method](params)
};